import React, { useState } from "react";
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { Navigation } from 'swiper/modules';
import axios from 'axios';

const Categorys = () => {
    const [categorys, setCategorys] = useState([]);

    const [search, setSearch] = useState('');
    const [filter, setFilter] = useState('');
    const fetchCategorys = useCallback(async () => {
        try {
            const res = await axios.get(
                `http://localhost:3036/api/v1/user/categorys?search=${search}&status=${filter}`,
                {
                    headers: { api_key: '123456789' },
                }
            );

            setTasks(Array.isArray(res.data.data.tasks) ? res.data.data.tasks : []);
            setTotalPages(res.data.data.totalPages || 0);
        } catch (error) {
            console.error('Error fetching tasks:', error);
            setCategorys([]);
        }
    }, [search, filter]);
    useEffect(() => {
        fetchCategorys();
    }, [fetchCategorys]);


    const categories = [
        { name: "Fruits & Veges", image: "/assets/images/category-thumb-1.jpg" },
        { name: "Breads & Sweets", image: "/assets/images/category-thumb-2.jpg" },
        { name: "Fruits & Veges", image: "/assets/images/category-thumb-3.jpg" },
        { name: "Beverages", image: "/assets/images/category-thumb-4.jpg" },
        { name: "Meat Products", image: "/assets/images/category-thumb-5.jpg" },
        { name: "Breads", image: "/assets/images/category-thumb-6.jpg" },
        { name: "Fruits & Veges", image: "/assets/images/category-thumb-7.jpg" },
        { name: "Breads & Sweets", image: "/assets/images/category-thumb-8.jpg" },
        { name: "Fruits & Veges", image: "/assets/images/category-thumb-1.jpg" },
        { name: "Beverages", image: "/assets/images/category-thumb-1.jpg" },
        { name: "Meat Products", image: "/assets/images/category-thumb-1.jpg" },
        { name: "Breads", image: "/assets/images/category-thumb-1.jpg" }
    ];

    return (
        <section className="py-5 overflow-hidden">
            <div className="container-lg">
                <div className="row">
                    <div className="col-md-12">
                        <div className="section-header d-flex flex-wrap justify-content-between mb-5">
                            <h2 className="section-title">Category</h2>
                            <div className="d-flex align-items-center">
                                <a href="#user" className="btn btn-primary me-2">View All</a>
                                <div className="swiper-buttons">
                                    <button className="swiper-prev category-carousel-prev btn btn-yellow">❮</button>
                                    <button className="swiper-next category-carousel-next btn btn-yellow">❯</button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                <Swiper
                    modules={[Navigation]}
                    spaceBetween={20}
                    slidesPerView={5}
                    navigation={{
                        nextEl: '.category-carousel-next',
                        prevEl: '.category-carousel-prev',
                    }}
                    loop={true}
                    breakpoints={{
                        0: { slidesPerView: 2 },
                        576: { slidesPerView: 3 },
                        768: { slidesPerView: 4 },
                        992: { slidesPerView: 5 },
                    }}
                >
                    {categories.map((cat, index) => (
                        <SwiperSlide key={index}>
                            <a href="category.html" className="nav-link text-center">
                                <img src={cat.image} className="rounded-circle" alt="Category Thumbnail" />
                                <h4 className="fs-6 mt-3 fw-normal category-title">{cat.name}</h4>
                            </a>
                        </SwiperSlide>
                    ))}
                </Swiper>
            </div>
        </section>
    );
}

export default Categorys;
